import React, { Component } from 'react';
import fire from './Fire';

class Login extends Component {
    constructor(props) {
      super(props);
      this.login = this.login.bind(this);
      this.handleChange = this.handleChange.bind(this);
      this.signup = this.signup.bind(this);
      this.state = {
          phone: '',
          cipher: '',

      }
    }
    login(event){
      event.preventDefault();
      fire.auth().signInWithEmailAndPassword(this.state.phone , this.state.cipher).then((u)=>{
      }).catch((error) => {
        console.log(error);
      });
    }
    signup(event){
      event.preventdefault();
      fire.auth().createUserWithEmailAndPassword(this.state.phone , this.state.cipher).then((u)=>{
      }).then((u)=>{console.log(u)})
      .catch((error)=>{
        console.log(error);
      })
    }

    handleChange(event) {
      this.setState({ [event.target.name]: event.target.value });
    }

    render() {
      return (
         <div className="col-md-6">
         <form>
        <div class="form-group">
         <label for="exampleInputPhone1">Número celular</label>
         <input value={this.state.email} onChange={this.handleChange} type="phone" name="phone" class="form-control" id="exampleInputPhone1" aria-describedby="phoneHelp" placeholder="Ingresa numero de celular" />
         <small id="emailHelp" class="form-text text-muted">We'll never share your email with anyone else.</small>
        </div>
         <div class="form-group">
        <label for="exampleInputCipher1">Código de Verificación</label>
        <input value={this.state.cipher} onChange={this.handleChange} type="cipher" name="cipher" class="form-control" id="exampleInputCipher1" placeholder="Ingresa el codigo de verificacion" />
        </div>
        <button type="submit" onClick={this.login} class="btn btn-primary">Login</button>
        <button onClick={this.signup} style={{marginLeft: '25px'}} className="btn btn-success">Signup</button>
   </form>

 </div>
    );
  }
}
export default Login;
